mapper.select:
select a.id,a.title,a.content,a.status,a.top,a.category_id,a.category_name,a.commentable,a.gmt_created, ae.good,ae.bad,ae.pageviews from article a left join article_ext ae on a.id = ae.article_id
AND title like "%"#{title,jdbcType=VARCHAR}"%"
AND a.id = #{id,jdbcType=BIGINT}

mapper.delete:
delete from article
where id in

    #{id}
